const binaryConversion = (str) => {
  if (str === '') return '';
  const strings = str.match(/.{1,8}/g);
  return strings.map((b) => String.fromCharCode(parseInt(b, 2))).join('');
};

// Tests

console.log(
  binaryConversion('011001010110010001100001011000100110100101110100'),
  'edabit'
);
console.log(
  binaryConversion('001100010011001000110011'),
  '123',
  'Ah! You have to handle numeric characters.'
);
console.log(
  binaryConversion('010010000110010101101100011011000110111100111111'),
  'Hello?',
  "You've got to also handle special characters."
);
console.log(
  binaryConversion(''),
  '',
  'You forgot to handle an empty string correnctly.'
);
console.log(
  binaryConversion(
    ''
  ),
  '!@#$%^&*()QWErtyUIOLdfgbbhnmIKBJKHIUO(?>?<~~~~~)(*&%^98713/-/*-*/',
  "It's those darn special characters!"
);
